// ProductForm.js

import React, { useState } from 'react';


const ProductForm = ({ onAddProduct }) => {
  const [newProduct, setNewProduct] = useState({
    name: '',
    description: '',
    image: '',
    sizes: '',
    colors: '',
    price: 0,
    stock: 0,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewProduct({
      ...newProduct,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Call the parent component's function to add the new product
    onAddProduct(newProduct);
    // Reset the form
    setNewProduct({
      name: '',
      description: '',
      image: '',
      sizes: '',
      colors: '',
      price: 0,
      stock: 0,
    });
  };

  return (
    <div className="product-form-container">
      <h1>Add New Product</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="name">Name:</label>
        <input type="text" id="name" name="name" value={newProduct.name} onChange={handleChange} required />

        <label htmlFor="description">Description:</label>
        <textarea id="description" name="description" value={newProduct.description} onChange={handleChange} required />

        <label htmlFor="image">Image URL:</label>
        <input type="text" id="image" name="image" value={newProduct.image} onChange={handleChange} required />

        <label htmlFor="sizes">Sizes:</label>
        <input type="text" id="sizes" name="sizes" value={newProduct.sizes} onChange={handleChange} required />

        <label htmlFor="colors">Colors:</label>
        <input type="text" id="colors" name="colors" value={newProduct.colors} onChange={handleChange} required />

        <label htmlFor="price">Price:</label>
        <input type="number" id="price" name="price" value={newProduct.price} onChange={handleChange} required />

        <label htmlFor="stock">Stock:</label>
        <input type="number" id="stock" name="stock" value={newProduct.stock} onChange={handleChange} required />

        <button type="submit">Add Product</button>
      </form>
    </div>
  );
}

export default ProductForm;
