// ProductForm.js

import React, { useState } from 'react';
import './ProductForm.css';
import Axios from 'axios'; // Import Axios
//import toast from 'react-toastify'; // Import toast

const ProductForm = () => {
  const [newProduct, setNewProduct] = useState({
    name: '',
    description: '',
    image: '',
    sizes: [],
    colors: [],
    price: 0,
    stock: 0,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewProduct({
      ...newProduct,
      [name]: name === 'sizes' || name === 'colors'
        ? value.split(',').map(item => item.trim())
        : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      // Send a POST request to your server using Axios
      const response = await Axios.post('http://localhost:5001/api/product/products', newProduct);
      //  toast.success('Product created successfully');
      
      // Reset the form
      setNewProduct({
        name: '',
        description: '',
        image: '',
        sizes: [],
        colors: [],
        price: 0,
        stock: 0,
      });
    } catch (error) {
      toast.error('Error creating product', error);
    }
  };

  return (
    <div className="product-form-container">
      <h1>Add New Product</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="name">Name:</label>
        <input type="text" id="name" name="name" value={newProduct.name} onChange={handleChange} required />

        <label htmlFor="description">Description:</label>
        <textarea id="description" name="description" value={newProduct.description} onChange={handleChange} required />

        <label htmlFor="image">Image URL:</label>
        <input type="text" id="image" name="image" value={newProduct.image} onChange={handleChange} required />

        <label htmlFor="sizes">Sizes (comma-separated):</label>
        <input type="text" id="sizes" name="sizes" value={newProduct.sizes.join(', ')} onChange={handleChange} required />

        <label htmlFor="colors">Colors (comma-separated):</label>
        <input type="text" id="colors" name="colors" value={newProduct.colors.join(', ')} onChange={handleChange} required />

        <label htmlFor="price">Price:</label>
        <input type="number" id="price" name="price" value={newProduct.price} onChange={handleChange} required />

        <label htmlFor="stock">Stock:</label>
        <input type="number" id="stock" name="stock" value={newProduct.stock} onChange={handleChange} required />

        <button type="submit">Add Product</button>
      </form>
    </div>
  );
}

export default ProductForm;
