// SellerOrders.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import styled from "styled-components";
import "./SellerOrders.css";

const SellerOrdersContainer = styled.div`
  max-width: 800px;
  margin: 20px auto;
`;

const SellerOrderItem = styled.div`
  border: 1px solid #ccc;
  border-radius: 8px;
  padding: 16px;
  margin-bottom: 16px;
`;

const SellerOrders = () => {
  const [sellerOrders, setSellerOrders] = useState([]);

  useEffect(() => {
    const fetchSellerOrders = async () => {
      try {
        // Adjust the API endpoint to fetch seller-specific orders
        const response = await axios.get("http://localhost:5001/api/seller/orders");
        setSellerOrders(response.data);
      } catch (error) {
        console.error("Error fetching seller orders:", error.message);
      }
    };

    fetchSellerOrders();
  }, []);

  const handleCompleteOrder = async (orderId) => {
    try {
      const response = await axios.put(`http://localhost:5001/api/orders/${orderId}/complete`);
      console.log("Response from marking order as completed:", response.data);
  
      // Update the sellerOrders state or refetch the data as needed...
    } catch (error) {
      console.error("Error marking order as completed:", error.message);
    }
  };

  return (
    <SellerOrdersContainer>
      <h1>Seller Orders</h1>
      {sellerOrders.map((order) => (
        <SellerOrderItem key={order.id}>
          <h2>Order ID: {order.id}</h2>
          {order.products.map((product) => (
            <div key={product.id}>
              <p>Product: {product.name}</p>
              <p>Quantity: {product.quantity}</p>
              <p>Price: ${product.price}</p>
            </div>
          ))}
          <p>Total Price: ${order.totalPrice}</p>
          {order.status !== 'completed' && (
            <button onClick={() => handleCompleteOrder(order.id)}>
              Mark as Completed
            </button>
          )}
        </SellerOrderItem>
      ))}
    </SellerOrdersContainer>
  );
};

export default SellerOrders;
