import React, { useState } from 'react';
import './ProductForm.css';
import Axios from 'axios';

const ProductForm = ({ onAddProduct }) => {
  const [newProduct, setNewProduct] = useState({
    title: '',
    description: '',
    image: '',
    sizes: [],
    colors: [],
    price: 0,
    stock: 0,
  });

  const [successMessage, setSuccessMessage] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;

    const updatedValue = name === 'sizes' || name === 'colors' ? value.split(',').map(item => item.trim()) : value;

    setNewProduct({
      ...newProduct,
      [name]: updatedValue,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await Axios.post('http://localhost:5001/api/product/products', newProduct);

      onAddProduct(response.data);

      // Set success message
      setSuccessMessage('Product has been added successfully!');

      // Reset the form
      setNewProduct({
        title: '',
        description: '',
        image: '',
        sizes: [],
        colors: [],
        price: 0,
        stock: 0,
      });

      // Clear success message after a few seconds
      setTimeout(() => {
        setSuccessMessage(null);
      }, 5000);
    } catch (error) {
      console.error('Error creating product', error);
    }
  };

  return (
    <div className="product-form-container">
      <h1>Add New Product</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="title">Title:</label>
        <input type="text" id="title" name="title" value={newProduct.title} onChange={handleChange} required />

        <label htmlFor="description">Description:</label>
        <textarea id="description" name="description" value={newProduct.description} onChange={handleChange} required />

        <label htmlFor="image">Image URL:</label>
        <input type="text" id="image" name="image" value={newProduct.image} onChange={handleChange} required />

        <label htmlFor="sizes">Sizes (comma-separated):</label>
        <input type="text" id="sizes" name="sizes" value={newProduct.sizes.join(', ')} onChange={handleChange} />

        <label htmlFor="colors">Colors (comma-separated):</label>
        <input type="text" id="colors" name="colors" value={newProduct.colors.join(', ')} onChange={handleChange} />

        <label htmlFor="price">Price:</label>
        <input type="number" id="price" name="price" value={newProduct.price} onChange={handleChange} required />

        <label htmlFor="stock">Stock:</label>
        <input type="number" id="stock" name="stock" value={newProduct.stock} onChange={handleChange} required />

        <button type="submit">Add Product</button>
      </form>
    </div>
  );
}

export default ProductForm;
