// ProductForm.js

import React, { useState } from 'react';
import './ProductForm.css';

const ProductForm = ({ onAddProduct }) => {
  const [newProduct, setNewProduct] = useState({
    title: '',
    description: '',
    image: '',
    category_id: '', // Make sure to set the correct category_id
    size: [], // Initialize as an empty array
    color: [], // Initialize as an empty array
    price: 0,
    in_stock: 0, // Initialize as a number
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewProduct({
      ...newProduct,
      [name]: name === 'size' || name === 'color'
        ? value.split(',').map(item => item.trim())
        : name === 'in_stock'
          ? parseInt(value, 10) // Convert to an integer for in_stock
          : value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Call the parent component's function to add the new product
    onAddProduct(newProduct);
    // Reset the form
    setNewProduct({
      title: '',
      description: '',
      image: '',
      category_id: '',
      size: [],
      color: [],
      price: 0,
      in_stock: 0,
    });
  };

  return (
    <div className="product-form-container">
      <h1>Add New Product</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="title">Title:</label>
        <input type="text" id="title" name="title" value={newProduct.title} onChange={handleChange} required />

        <label htmlFor="description">Description:</label>
        <textarea id="description" name="description" value={newProduct.description} onChange={handleChange} required />

        <label htmlFor="image">Image URL:</label>
        <input type="text" id="image" name="image" value={newProduct.image} onChange={handleChange} required />

        <label htmlFor="size">Size (comma-separated):</label>
        <input type="text" id="size" name="size" value={newProduct.size.join(', ')} onChange={handleChange} required />

        <label htmlFor="color">Color (comma-separated):</label>
        <input type="text" id="color" name="color" value={newProduct.color.join(', ')} onChange={handleChange} required />

        <label htmlFor="price">Price:</label>
        <input type="number" id="price" name="price" value={newProduct.price} onChange={handleChange} required />

        <label htmlFor="in_stock">In Stock:</label>
        <input type="number" id="in_stock" name="in_stock" value={newProduct.in_stock} onChange={handleChange} required />

        <button type="submit">Add Product</button>
        
      </form>
    </div>
  );
}

export default ProductForm;
