// OrderList.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import styled from "styled-components";
import "./OrderList.css";

const OrderListContainer = styled.div`
  max-width: 800px;
  margin: 20px auto; /* Add margin for separation from ProductList */
`;

const OrderItem = styled.div`
  border: 1px solid #ccc;
  border-radius: 8px;
  padding: 16px;
  margin-bottom: 16px;
`;

const OrderButton = styled.button`
  background-color: #4caf50;
  color: white;
  padding: 10px;
  border: none;
  border-radius: 4px;
  cursor: pointer;
`;

const OrderList = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await axios.get("http://localhost:5001/api/orders/");
        setOrders(response.data);
      } catch (error) {
        console.error("Error fetching orders:", error.message);
      }
    };

    fetchOrders();
  }, []);

  const handleCompleteOrder = async (orderId) => {
    try {
      const response = await axios.put(`http://localhost:5001/api/orders/${orderId}/complete`);
      console.log("Response from marking order as completed:", response.data);
  
      // Rest of the code...
    } catch (error) {
      console.error("Error marking order as completed:", error.message);
    }
  };

  return (
    <OrderListContainer>
      <h1>Order List</h1>
      {orders.map((order) => (
        <OrderItem key={order.id}>
          <h2>Order ID: {order.id}</h2>
          {order.products.map((product) => (
            <div key={product.id}>
              <p>Product: {product.name}</p>
              <p>Quantity: {product.quantity}</p>
              <p>Price: ${product.price}</p>
            </div>
          ))}
          <p>Total Price: ${order.totalPrice}</p>
          {order.status !== 'completed' && (
            <OrderButton onClick={() => handleCompleteOrder(order.id)}>
              Mark as Completed
            </OrderButton>
          )}
        </OrderItem>
      ))}
    </OrderListContainer>
  );
};

export default OrderList;
