import React, { useState } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import AddProduct from "./AddProduct"; // Import your AddProduct component
import SellerProducts from "./SellerProducts"; // Import your SellerProducts component
import SellerOrders from "./SellerOrders"; // Import your SellerOrders component

const SellerDashboard = () => {
  const [selectedOption, setSelectedOption] = useState("home");

  const handleOptionChange = (option) => {
    setSelectedOption(option);
  };

  return (
    <Container>
      <Welcome>Welcome to Seller Dashboard</Welcome>
      <Menu>
        <MenuItem to="/" selected={selectedOption === "home"} onClick={() => handleOptionChange("home")}>
          Home
        </MenuItem>
        <MenuItem to="/AddProduct" selected={selectedOption === "addProduct"} onClick={() => handleOptionChange("addProduct")}>
          Add a Product
        </MenuItem>
        <MenuItem to="/SellerProducts" selected={selectedOption === "seeAllProducts"} onClick={() => handleOptionChange("seeAllProducts")}>
          See All Products
        </MenuItem>
        <MenuItem to="/SellerOrders" selected={selectedOption === "seeAllOrders"} onClick={() => handleOptionChange("seeAllOrders")}>
          See All Orders
        </MenuItem>
      </Menu>

      <Content>
        {/* The content of the selected page will be rendered here */}
      </Content>
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  max-width: 800px;
  margin: 20px auto;
  flex-direction: column;
  align-items: center;
`;

const Welcome = styled.h2`
  text-align: center;
  margin-bottom: 20px;
`;

const Menu = styled.nav`
  display: flex;
  flex-direction: column;
  border: 1px solid black; /* Added black border to the menu */
`;

const MenuItem = styled(Link)`
  display: block;
  padding: 20px;
  cursor: pointer;
  background-color: ${(props) => (props.selected ? "#eee" : "transparent")};
  color: black;
  text-align: center;
  text-decoration: none;
  border-bottom: 1px solid black; /* Added black border to menu items */

  &:hover {
    background-color: #eee;
  }
`;

const Content = styled.div`
  flex: 1;
  padding: 20px;
`;

export default SellerDashboard;
