// ProductForm.js

import React, { useState } from 'react';
import './ProductForm.css';

const ProductForm = ({ onAddProduct }) => {
  
  const [newProduct, setNewProduct] = useState({
    name: '',
    description: '',
    image: '',
    sizes: [],
    colors: [],
    price: 0,
    stock: 0,
  });

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await Axios.get('http://localhost:5001/api/products');
        const data = response.data;
        setProducts(data);
      } catch (error) {
        console.error('Error fetching products', error);
      }
    };

    // Call fetchProducts when the component mounts
    fetchProducts();
  }, []);

  const handleAddProduct = async () => {
    try {
      // Make an HTTP POST request to add the new product
      const response = await Axios.post('http://localhost:5001/api/products', newProduct);
      const addedProduct = response.data;

      // Update the products state with the new product
      setProducts((prevProducts) => [...prevProducts, addedProduct]);

      // Reset the form
      setNewProduct({
        name: '',
        description: '',
        image: '',
        sizes: [],
        colors: [],
        price: 0,
        stock: 0,
      });
    } catch (error) {
      console.error('Error adding product', error);
    }
  };


  return (
    <div className="product-form-container">
      <h1>Add New Product</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="name">Name:</label>
        <input type="text" id="name" name="name" value={newProduct.name} onChange={handleChange} required />

        <label htmlFor="description">Description:</label>
        <textarea id="description" name="description" value={newProduct.description} onChange={handleChange} required />

        <label htmlFor="image">Image URL:</label>
        <input type="text" id="image" name="image" value={newProduct.image} onChange={handleChange} required />

        <label htmlFor="sizes">Sizes:</label>
        <input type="text" id="sizes" name="sizes" value={newProduct.sizes} onChange={handleChange} required />

        <label htmlFor="colors">Colors:</label>
        <input type="text" id="colors" name="colors" value={newProduct.colors} onChange={handleChange} required />

        <label htmlFor="price">Price:</label>
        <input type="number" id="price" name="price" value={newProduct.price} onChange={handleChange} required />

        <label htmlFor="stock">Stock:</label>
        <input type="number" id="stock" name="stock" value={newProduct.stock} onChange={handleChange} required />

        <button type="submit">Add Product</button>
      </form>
    </div>
  );
}

export default ProductForm;
