import React, { useState } from "react";
import { Link, Route, Routes } from "react-router-dom";
import styled from "styled-components";
import AddProduct from "./AddProduct"; // Import your AddProduct component
import SellerProducts from "./SellerProducts"; // Import your SellerProducts component
import SellerOrders from "./SellerOrders"; // Import your SellerOrders component

const SellerDashboard = () => {
  const [selectedOption, setSelectedOption] = useState("home");

  const handleOptionChange = (option) => {
    setSelectedOption(option);
  };

  return (
    <Container>
      <Menu>
        <MenuItem to="/" onClick={() => handleOptionChange("home")}>
          Home
        </MenuItem>
        <MenuItem to="/add-product" onClick={() => handleOptionChange("addProduct")}>
          Add a Product
        </MenuItem>
        <MenuItem to="/seller-products" onClick={() => handleOptionChange("seeAllProducts")}>
          See All Products
        </MenuItem>
        <MenuItem to="/seller-orders" onClick={() => handleOptionChange("seeAllOrders")}>
          See All Orders
        </MenuItem>
      </Menu>

      <Content>
        <Routes>
           <Route path="/" element={<Home />} />
          <Route path="/add-product" element={<AddProduct />} />
          <Route path="/seller-products" element={<SellerProducts />} />
          <Route path="/seller-orders" element={<SellerOrders />} /> 
        </Routes>
      </Content>
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  max-width: 800px;
  margin: 20px auto;
`;

const Menu = styled.nav`
  flex: 1;
  border-right: 1px solid #ccc;
`;

const MenuItem = styled(Link)`
  display: block;
  padding: 20px;
  cursor: pointer;
  background-color: ${(props) => (props.to === "/" ? "#eee" : "transparent")};
  text-decoration: none;
  color: black;

  &:hover {
    background-color: #eee;
  }
`;

const Content = styled.div`
  flex: 3;
  padding: 20px;
`;

const Home = () => (
  <div>
    <h2>Welcome to Seller Dashboard</h2>
    <p>Explore the options in the menu to manage your products and orders.</p>
  </div>
);

export default SellerDashboard;
