                    </Details>
                  </ProductDetail>
                  <PriceDetail>
                    <ProductAmountContainer>
                      {/* You can add logic to increase/decrease the quantity here */}
                      <ProductAmount>
                        <b>Quantity: </b>
                        {product.products[0].quantity}
                      </ProductAmount>
                    </ProductAmountContainer>
                    <ProductPrice>${product.products[0].price}</ProductPrice>
                    <DeleteIcon onClick={deletefromcart} /> 
