import styled from "styled-components";
import { mobile } from "../responsive";
import { useDispatch } from "react-redux";
import { setcategoryInfo } from "../store/slices/categorySlice";
import { useNavigate } from "react-router-dom";

const Container = styled.div`
  flex: 1;
  margin: 3px;
  height: 70vh;
  position: relative;
`;

const Image = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  ${mobile({ height: "20vh" })}

`;

const Info = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const Title = styled.h1`
    color:white;
    margin-bottom: 20px;
`;

const Button = styled.button`
    border:none;
    padding: 10px;
    background-color: white;
    color:gray;
    cursor: pointer;
    font-weight: 600;
`;

const CategoryItem = ({ item }) => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleShop = (id) => {
    const req = { "id": id };
    dispatch(setcategoryInfo(req));
    navigate("/product");
  };
  
  return (
    <Container>
      <Image src={item.img} />
      <Info>
        <Title>{item.title}</Title>
        {/* Pass a function reference to onClick */}
        <Button onClick={() => handleShop(item.id)}>SHOP NOW</Button>
      </Info>
    </Container>
  )}

export default CategoryItem;
