// OrderTable.js
import React from 'react';
import './SellerOrders.css';
import SellerNavbar from './SellerNavbar';
import Axios from 'axios';

const SellerOrders = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    // Fetch orders from the server when the component mounts
    const fetchOrders = async () => {
      try {
        // Make a GET request to your backend API endpoint
        const response = await Axios.get('http://localhost:5001/api/orders/');
        // Extract the data from the response
        const data = response.data;
        // Update the state with the fetched orders
        setOrders(data);
      } catch (error) {
        console.error('Error fetching orders', error);
      }
    };

    fetchOrders();
  }, []); 
  
  const handleCompleteOrder = async (orderId) => {
    try {
      // Make a PUT request to mark the order as completed
      await Axios.put(`http://localhost:5001/api/orders/${orderId}/complete`);
      // Refresh the orders after the update
      fetchOrders();
    } catch (error) {
      console.error('Error completing order', error);
    }
  };


  return (
    <div>
    <SellerNavbar/>
    <div className="order-table-container">
      
      <h1 className='order-table-header'>Orders</h1>
      <table className="order-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Customer ID</th>
            <th>Total Amount</th>
            <th>Shipping Address</th>
            <th>Status</th>
            <th>Order Items</th>
            <th>Created At</th>
            <th>Updated At</th>
          </tr>
        </thead>
        <tbody>
          {orders.map(order => (
            <tr key={order.id}>
              <td>{order.id}</td>
              <td>{order.customerId}</td>
              <td>{order.totalAmount}</td>
              <td>{order.shippingAddress}</td>
              <td>{order.status}</td>
              <td>
                <ul>
                  {order.orderItems.map(item => (
                    <li key={item.id}>
                      {item.name} - {item.quantity || 1} {item.size && `(${item.size})`}
                    </li>
                  ))}
                </ul>
              </td>
              <td>{order.createdAt}</td>
              <td>{order.updatedAt}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
    </div>
  );
};

export default SellerOrders;
