import React, { useEffect, useState } from "react";
import axios from "axios";
import styled from "styled-components";
import { useDispatch, useSelector } from "react-redux";
//import { setproductInfo } from "../../../store/slices/productSlice"; // Correct import statement


const SellerProductContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
`;

const SellerProductItem = styled.div`
  border: 1px solid #ccc;
  border-radius: 8px;
  padding: 16px;
  margin-bottom: 16px;
`;

const SellerProductImage = styled.img`
  max-width: 100%;
  height: auto;
  border-radius: 8px;
  margin-bottom: 8px;
`;

const SellerProducts = () => {
  const [sellerProducts, setSellerProducts] = useState([]);
  const dispatch = useDispatch();
  const products = useSelector((state) => state.product.productInfo);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5001/api/product/products");
        // Dispatch the action with the fetched data
        // dispatch(setproductInfo(response.data));
        setSellerProducts(response.data);
      } catch (error) {
        console.error("Error fetching seller products:", error.message);
      }
    };

    fetchData();
  }, [dispatch]);

  return (
    <SellerProductContainer>
      <h1>Seller Product List</h1>
      {sellerProducts.map((product) => (
        <SellerProductItem key={product.id}>
          <SellerProductImage src={product.image} alt={product.name} />
          <h2>{product.name}</h2>
          <p>{product.description}</p>
          <p>Price: ${product.price}</p>
          {/* Additional Seller-specific information */}
          <p>Stock: {product.stock}</p>
          <p>Rating: {product.rating}</p>
        </SellerProductItem>
      ))}
    </SellerProductContainer>
  );
};

export default SellerProducts;
