import React, { useState } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import AddProduct from "./AddProduct"; // Import your AddProduct component
import SellerProducts from "./SellerProducts"; // Import your SellerProducts component
import SellerOrders from "./SellerOrders"; // Import your SellerOrders component
import { AppBar, Tab, Tabs } from "@mui/material";

const SellerDashboard = () => {
  const [selectedOption, setSelectedOption] = useState("home");

  const handleOptionChange = (event, newValue) => {
    setSelectedOption(newValue);
  };

  return (
    <Container>
      <AppBar position="static">
        <Tabs value={selectedOption} onChange={handleOptionChange}>
          <Tab label="Home" value="home" component={Link} to="/" />
          <Tab label="Add a Product" value="addProduct" component={Link} to="/seller/addproduct" />
          <Tab label="See All Products" value="seeAllProducts" component={Link} to="/seller/products" />
          <Tab label="See All Orders" value="seeAllOrders" component={Link} to="/seller/orders" />
        </Tabs>
      </AppBar>

      <Content>
        {/* The content of the selected page will be rendered here */}
      </Content>
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  flex-direction: column;
  max-width: 800px;
  margin: 20px auto;
`;

const Content = styled.div`
  flex: 1;
  padding: 20px;
`;

export default SellerDashboard;
