// ProductList.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import styled from "styled-components";
import { useDispatch, useSelector } from "react-redux";
import { setproductInfo } from "../../../store/slices/productSlice"; // Correct import statement
import "./ProductList.css";

const ProductListContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
`;

const ProductItem = styled.div`
  border: 1px solid #ccc;
  border-radius: 8px;
  padding: 16px;
  margin-bottom: 16px;
`;

const ProductImage = styled.img`
  max-width: 100%;
  height: auto;
  border-radius: 8px;
  margin-bottom: 8px;
`;

const ProductList = () => {
    
    const [prod, setProd] = useState([])
  const dispatch = useDispatch();
  const products = useSelector((state) => state.product.productInfo);

  useEffect(() => {
    const fetchData = async () => {
        console.log("niaklsd")
      try {
        const response = await axios.get("http://localhost:5001/api/product/products");
        // Dispatch the action with the fetched data
      //  dispatch(setproductInfo(response.data));
      setProd(response.data)
      console.log(response.data)
      console.log("niaklsd")
      console.log(prod)
      } catch (error) {
        console.error("Error fetching products:", error.message);
      }
    };
    
    fetchData();
  }, [dispatch]);

  return (
    <ProductListContainer>
      <h1>Product List</h1>
      {prod.map((product) => (
        <ProductItem key={product.id}>
          <ProductImage src={product.image} alt={product.name} />
          <h2>{product.name}</h2>
          <p>{product.description}</p>
          <p>Price: ${product.price}</p>
        </ProductItem>
      ))}
    </ProductListContainer>
  );
};

export default ProductList;
