import React, { useState } from 'react';
import './ProductForm.css';
import Axios from 'axios';

const ProductForm = ({ onAddProduct }) => {
  const [newProduct, setNewProduct] = useState({
    title: '',
    description: '',
    image: '',
    sizes: [], // Change to an array
    colors: [], // Change to an array
    price: 0,
    stock: 0,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;

    // If the field is sizes or colors, split the comma-separated string into an array
    const updatedValue = name === 'sizes' || name === 'colors' ? value.split(',').map(item => item.trim()) : value;

    setNewProduct({
      ...newProduct,
      [name]: updatedValue,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Make an API request to add the new product
      const response = await Axios.post('http://localhost:5001/api/products', newProduct);
      
      // Call the parent component's function to add the new product locally
      onAddProduct(response.data);
      
      // Reset the form
      setNewProduct({
        title: '',
        description: '',
        image: '',
        sizes: [],
        colors: [],
        price: 0,
        stock: 0,
      });
    } catch (error) {
      console.error('Error creating product', error);
    }
  };

  return (
    <div className="product-form-container">
      <h1>Add New Product</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="title">Title:</label>
        <input type="text" id="title" name="title" value={newProduct.title} onChange={handleChange} required />

        <label htmlFor="description">Description:</label>
        <textarea id="description" name="description" value={newProduct.description} onChange={handleChange} required />

        <label htmlFor="image">Image URL:</label>
        <input type="text" id="image" name="image" value={newProduct.image} onChange={handleChange} required />

        <label htmlFor="sizes">Sizes (comma-separated):</label>
        <input type="text" id="sizes" name="sizes" value={newProduct.sizes.join(', ')} onChange={handleChange} required />

        <label htmlFor="colors">Colors (comma-separated):</label>
        <input type="text" id="colors" name="colors" value={newProduct.colors.join(', ')} onChange={handleChange} required />

        <label htmlFor="price">Price:</label>
        <input type="number" id="price" name="price" value={newProduct.price} onChange={handleChange} required />

        <label htmlFor="stock">Stock:</label>
        <input type="number" id="stock" name="stock" value={newProduct.stock} onChange={handleChange} required />

        <button type="submit">Add Product</button>
      </form>
    </div>
  );
}

export default ProductForm;
