import React, { useState } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import { AppBar, Tabs, Tab, Container as MuiContainer, CssBaseline, ThemeProvider, createTheme } from "@mui/material";
import { Home as HomeIcon, AddCircleOutline as AddCircleOutlineIcon, ShoppingCart as ShoppingCartIcon, Assignment as AssignmentIcon } from "@mui/icons-material";

import AddProduct from "./AddProduct"; // Import your AddProduct component
import SellerProducts from "./SellerProducts"; // Import your SellerProducts component
import SellerOrders from "./SellerOrders"; // Import your SellerOrders component

const theme = createTheme();

const SellerDashboard = () => {
  const [selectedOption, setSelectedOption] = useState("home");

  const handleOptionChange = (_, newValue) => {
    setSelectedOption(newValue);
  };

  const menuItems = [
    { label: "Home", value: "home", icon: <HomeIcon /> },
    { label: "Add a Product", value: "addProduct", icon: <AddCircleOutlineIcon /> },
    { label: "See All Products", value: "seeAllProducts", icon: <ShoppingCartIcon /> },
    { label: "See All Orders", value: "seeAllOrders", icon: <AssignmentIcon /> },
  ];

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container>
        <AppBar position="sticky" elevation={0} color="transparent">
          <Tabs value={selectedOption} onChange={handleOptionChange} textColor="inherit">
            {menuItems.map((item) => (
              <Tab key={item.value} label={item.label} value={item.value} icon={item.icon} component={Link} to={`/seller/${item.value}`} />
            ))}
          </Tabs>
        </AppBar>

        <Content>
          {/* The content of the selected page will be rendered here */}
        </Content>
      </Container>
    </ThemeProvider>
  );
};

const Container = styled(MuiContainer)`
  display: flex;
  flex-direction: column;
  max-width: 800px;
  margin: 20px auto;
  padding: 0;
`;

const Content = styled.div`
  flex: 1;
  padding: 20px;
`;

export default SellerDashboard;
