import React, { useState } from "react";
import styled from "styled-components";
import AddProduct from "./AddProduct";
import SellerProducts from "./SellerProducts";
import SellerOrders from "./SellerOrders";

const SellerDashboard = () => {
  const [selectedOption, setSelectedOption] = useState("home");

  const handleOptionChange = (option) => {
    setSelectedOption(option);
  };

  const renderComponent = () => {
    switch (selectedOption) {
      case "home":
        return <Home />;
      case "addProduct":
        return <AddProduct />;
      case "seeAllProducts":
        return <SellerProducts />;
      case "seeAllOrders":
        return <SellerOrders />;
      default:
        return null;
    }
  };

  return (
    <Container>
      <Menu>
        <MenuItem onClick={() => handleOptionChange("home")}>Home</MenuItem>
        <MenuItem onClick={() => handleOptionChange("addProduct")}>Add a Product</MenuItem>
        <MenuItem onClick={() => handleOptionChange("seeAllProducts")}>See All Products</MenuItem>
        <MenuItem onClick={() => handleOptionChange("seeAllOrders")}>See All Orders</MenuItem>
      </Menu>

      <Content>{renderComponent()}</Content>
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  max-width: 800px;
  margin: 20px auto;
  background-color: #f9f9f9;
  border-radius: 8px;
  overflow: hidden;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
`;

const Menu = styled.nav`
  flex: 1;
  border-right: 1px solid #ccc;
`;

const MenuItem = styled.div`
  display: block;
  padding: 20px;
  cursor: pointer;
  background-color: ${(props) => (props.selected ? "#eee" : "transparent")};
  color: black;
  border-bottom: 1px solid #ccc; /* Added border to visually distinguish items */

  &:hover {
    background-color: #eee;
  }
`;


const Content = styled.div`
  flex: 3;
  padding: 20px;
`;

const Home = styled.div`
  text-align: center;
`;

export default SellerDashboard;
